jQuery(window).on( 'pluginCarouselReady', function(){
        $('#oc-features').owlCarousel({
            items: 1,
            margin: 60,
            nav: true,
            navText: ['<i class="icon-line-arrow-left"></i>','<i class="icon-line-arrow-right"></i>'],
            dots: false,
            stagePadding: 30,
            responsive:{
                768: { items: 2 },
                1200: { stagePadding: 200 }
            },
        });
    });
